import ReactDOM from 'react-dom';
import books from '../models/books.json';
import './Book.css'

const Book = (props) => {
  let {volumeInfo: {title, authors, description, imageLinks: {thumbnail}}} = books;
    return (
      <div className='bookContainer'>
      <img src= {thumbnail} alt = {title}></img>
      <h2>{title}</h2>
      <p>{authors}</p>
      <p>{description}</p>
    </div>
    );
}

